import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
const UserLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/login", {
        email_id: email,
        password: password,
      });

      setError("");
      const { token } = response.data;
      if (token) {
        // Save the token in a cookie
        // Cookies.set('token', token, { expires: 1 }); // Token expires in 1 day

        // Redirect to the home page (replace '/home' with the actual path)
        // You can use React Router for more advanced navigation.
        // navigate("/home");
      }
    } catch (err) {
      setError("Invalid credentials");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        height: "90vh",
      }}
    >
      <h2>Login</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <input
        type="text"
        placeholder="Email"
        value={email}
        style={{ marginBottom: "5px", padding: "2px" }}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        style={{ marginBottom: "5px", padding: "2px" }}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={handleLogin}
        style={{ padding: "10px" }}
      >
        Login
      </button>
    </div>
  );
};

export default UserLogin;
